    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}{{ product.name }} - Sneakers{% endblock %}

{% block content %}
    <h1>{{ product.name }}</h1><br>
    <div class=\"row\">
        <div class=\"col-md-5 mb-5\">
            <img src=\"/uploads/{{ product.illustration }}\" alt=\"{{ product.name }}\" class=\"img-fluid\">
        </div>
        <div class=\"col-md-7 my-auto\">
            <h3>{{ product.name }}</h3>
            <p>{{ product.subtitle }}</p>
            <span class=\"product-page-price\">{{ (product.price/100)|number_format(2,',','.') }}€</span>
            <hr>
            <p>
                {{ product.description }}
            </p>
            <a href=\"{{ path('add_to_cart', {'id': product.id}) }}\" class=\"btn btn-primary\">Ajouter au panier</a>

        </div>

    </div>

{% endblock %}
", "product/show.html.twig", "/home/valence/Cours/Examen/PPEWeb1/Sneakers/templates/product/show.html.twig");
